kickstart.post:
/bin/mkdir -m 0755 /root/rolltests


#!/usr/bin/perl -w
# hadoop roll installation test.  Usage:
# hadoop.t [nodetype]
#   where nodetype is one of "Compute", "Dbnode", "Frontend" or "Login"
#   if not specified, the test assumes either Compute or Frontend

use Test::More qw(no_plan);

my $appliance = $#ARGV >= 0 ? $ARGV[0] :
                -d '/export/rocks/install' ? 'Frontend' : 'Compute';
my $installedOnAppliancesPattern = 'Compute|Dbnode';
my $hadoopHome = '/opt/hadoop';
my $isInstalled = -d $hadoopHome;
my $output;

# hadoop-install.xml
if($appliance =~ /$installedOnAppliancesPattern/) {
  ok($isInstalled, 'hadoop installed');
  ok(-d "$hadoopHome/hadoop/contrib/myHadoop", 'myHadoop installed');
} else {
  ok(! $isInstalled, 'hadoop not installed');
}
SKIP: {
  skip 'hadoop roll not installed', 3 if ! $isInstalled;
  ok(-l "$hadoopHome/hadoop", 'hadoop symbolic link created');
  ok(-d "$hadoopHome/hadoop/logs", 'hadoop log dir created');
  like(`ls -ld $hadoopHome/hadoop/logs`, qr/rwxrwxrwx/,
       'hadoop log dir world-writable');
}
