kickstart.post:
/bin/mkdir -m 0755 /root/rolltests


#!/usr/bin/perl -w

use Test::More qw(no_plan);

my $appliance = $#ARGV >= 0 ? $ARGV[0] :
                -d '/export/rocks/install' ? 'Frontend' : 'Compute';
my $isCompute = $appliance eq 'Compute';
my $isDbnode = $appliance eq 'Dbnode';
my $isFe = $appliance eq 'Frontend';
my $isLogin = $appliance eq 'Login';
my $output;

if ($isCompute || $isDbnode) {
  ok(-d '/opt/hadoop', 'hadoop installed');
} else {
  ok(! -d '/opt/hadoop', 'hadoop not installed');
}
SKIP: {

  skip 'hadoop roll not installed', 4 if ! -d '/opt/hadoop';
  ok(-l '/opt/hadoop/hadoop', 'hadoop symbolic link created');
  ok(-d '/opt/hadoop/hadoop/logs', 'hadoop log dir created');
  like(`ls -ld /opt/hadoop/hadoop/logs`, qr/rwxrwxrwx/, 'log dir world-writable');
  ok(-d '/opt/hadoop/hadoop/contrib/myHadoop', 'myHadoop installed');

}
